CREATE TABLE IF NOT EXISTS ADDRESSES (ID UUID PRIMARY KEY, STREET VARCHAR(128) NOT NULL, HOUSE_NUMBER VARCHAR(128) NOT NULL, ZIP_CODE VARCHAR(128) NOT NULL, CITY VARCHAR(128) NOT NULL);
CREATE TABLE IF NOT EXISTS USERS (ID UUID PRIMARY KEY, FIRST_NAME VARCHAR(128) NOT NULL, LAST_NAME VARCHAR(128) NOT NULL, PHONE_NUMBER VARCHAR(128) NOT NULL, USERNAME VARCHAR(24) NOT NULL, ADDRESS UUID NOT NULL, CONSTRAINT FK_USERS_ADDRESS__ID FOREIGN KEY (ADDRESS) REFERENCES ADDRESSES(ID) ON DELETE RESTRICT ON UPDATE RESTRICT);
